(4, 3, 'ASUS Z890 AYW Gaming WiFi W ATX Motherbo', '685d9bf00c0030.72789131.webp', '  <li><strong>Ready for Advanced AI PCs:</strong> Designed for the future of AI computing, with the power and connectivity needed for demanding AI applications</li>\r\n  <li><strong>Intel LGA 1851 Socket:</strong> Ready for Intel Core Ultra Processors (series 2)</li>\r\n  <li><strong>Robust Power Solution:</strong> 12+1+2+1 80A DrMOS power stages with ProCool power connectors, 6-layer PCB, alloy chokes and durable capacitors for stable power delivery</li>\r\n  <li><strong>Next-gen Connectivity:</strong> DDR5 memory, Wi-Fi 6, PCIe 5.0, rear USB 20Gbps Type-C, front panel USB 20Gbps Type-C with up to 30W PD/PPS Fast-charge and USB Wattage Watcher, Thunderbolt (USB4) header support</li>\r\n  <li><strong>Optimized Thermal Design:</strong> Large VRM heatsinks, M.2 and PCH heatsinks, hybrid fan headers and Fan Xpert 4 with AI Cooling II</li>\r\n  <li><strong>Latest M.2 Support:</strong> One onboard PCIe 5.0 M.2 slot and three PCIe 4.0 M.2 slots, all with cooling solutions</li>\r\n  <li><strong>DIY Friendly Design:</strong> PCIe Slot Q-Release, M.2 Q-Latch, Q-Dashboard, Q-LED, BIOS FlashBack button, BIOS FlashBack LED and pre-mounted I/O shield</li>\r\n\r\n', 199),
(5, 4, 'Kingston Fury Beast RGB 8GB 3200MT/s DDR', '685d9d61e47ce0.69052550.webp', '<ul>\r\n<li class=\"a-spacing-mini\"><span class=\"a-list-item\" style=\"font-size: 14pt;\">Sleek low-profile RGB lighting</span></li>\r\n<li class=\"a-spacing-mini\"><span class=\"a-list-item\" style=\"font-size: 14pt;\">Patented Kingston FURY Infrared Sync Technology</span></li>\r\n<li class=\"a-spacing-mini\"><span class=\"a-list-item\" style=\"font-size: 14pt;\">Intel XMP Certified and XMP-Ready</span></li>\r\n<li class=\"a-spacing-mini\"><span class=\"a-list-item\" style=\"font-size: 14pt;\">Ready for AMD Ryzen</span></li>\r\n<li class=\"a-spacing-mini\"><span class=\"a-list-item\" style=\"font-size: 14pt;\">Plug N Play functionality at 2666MT/s</span></li>\r\n</ul>', 22),
